import React,{useState, useEffect} from 'react'

function Books() {
 const [books, setBooks] = useState([]);
 const bookurl = "/books";

 useEffect(async()=>{
  getBooks();
 },[])

 const getBooks = ()=>{
  const response = fetch(bookurl)
   .then(response=>response.json())
   .catch((err) => console.log(err))
   setBooks(response)
 }
 return (
  <div>
    {books.map((book, index)=>
     <li>{book.author}</li>
    )}
  </div>
 )
}

export default Books
