import React, { useState, useEffect } from "react";
import axios from "axios";


function Create() {
 const [post, setPosts] = useState([]);

 const onSubmit = (e) => {
   e.preventDefault();
   axios
     .post("/blog/create", { item: e.target.itemName.value })
     .then((res) => {
       var temp = [...post];
       temp.push({ Title: e.target.itemName.value });
       setPosts(temp);
     })
     .catch((err) => {
       alert(err);
     });
 };

  return(
   <div>
    <h3>Posts: </h3>
     <form onSubmit={onSubmit}>
       <input type="text" name="itemName" placeholder="Title" />
       <input type="text" name="itemName" placeholder="Creator" />
       <input type="textField" name="itemName" placeholder="Body" />
       <input type="date" name="itemName" placeholder="Created_at" />
       <input type="number" name="itemName" placeholder="Likes" />
       <input type="submit" />
     </form>
     </div>

  )
}

export default Create;
