import React, {useState, useEffect} from 'react'

function Blog() {

  const[blog,setBlog] = useState([]);
  const blogurl = "/blog/blog";

  const[createBlog, setCreateBlog] = useState([])
  const createBlogurl = "/blog/blog/create"

  useEffect(async()=>{
    getBlogPosts();
  },[])
  const getBlogPosts = async()=>{
   const response = await fetch(blogurl)
   const responsejson = await response.json()
   setBlog(responsejson)
  }

  useEffect(async()=>{
    createBlogPost()
  },[])

  const createBlogPost = async()=>{
    const post = await fetch(createBlogurl)
    const postjson = post.json();
    setCreateBlog(postjson);
  }


 return (
   <>
   <h1>Blog Posts</h1>
   <div className="blog">
     <h3>Title</h3>
     <h3>Creator</h3>
     <h3>Content</h3>
     <h3>Created_at</h3>
     <h3>Likes</h3>
     
     {blog.map((blog, index) => (
       <>
         <h2>{blog.Title}</h2>
         <h2>{blog.Creator}</h2>
         <h2>{blog.Body}</h2>
         <h2>{blog.Created_at}</h2>
         <h2>{blog.Likes}</h2>
         
      </>
      
     ))}
     </div>

     <div className = "createblog">
       <form action="#" method="POST">
         <label htmlFor="Title">{createBlog.Title}</label>
       </form>
     </div>
   </>
 );
}

export default Blog;
