import React, { useState, useEffect } from "react";
import axios from "axios";


function Create() {
 const [post, setPosts] = useState([]);

const onSubmit = (e) => {
   e.preventDefault();
   axios
     .post("/blog/create", post)
     .then((res) => {
       var temp = [...post];
       temp.push( post);
       setPosts(temp);
     })
     .catch((err) => {
       alert(err);
     });
 };

  return(
    <div>
      <h3>Posts: </h3>
      <form onSubmit={onSubmit}>
        {post.map((item) => 
          <div>
            <input type="text" name="Title" placeholder="Title" />
            <input type="text" name="" placeholder="Creator" />
            <input type="textField" name="" placeholder="Body" />
            <input type="date" name="" placeholder="Created_at" />
            <input type="number" name="" placeholder="Likes" />
            <input type="submit" />
          </div>
          )}
         </form>
    </div>
  ) 
  }
 
export default Create;
