import React, { useState, useEffect } from "react";

const userUrl = "/api/users";

function Customers() {
  const [userData, setUserData] = useState({});

  useEffect(() => {
    getGitHubUserWithFetch();
  }, []);

  const getGitHubUserWithFetch = async () => {
    const response = await fetch(userUrl);
    const jsonData = await response.json();
    setUserData(jsonData);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Dexter</h2>
      </header>
      <div className="user-container">
          {userData.map((user,index)=>
          <ul>
    
            <h3 className="info-item" key={user.index}>{user.firstName}</h3>   
            <h3 className="info-item" key={user.index}>{user.lastName}</h3>
            <h3 className="info-item" key={user.index}>{user.email}</h3>
            <h3 className="info-item" key={user.index}>{user.userName}</h3>
            <h3 className="info-item" key={user.index}>{user.password}</h3>   
          </ul>
            
             )} 
      </div>
    </div>
  );
}

export default Customers;
